let monsterData;fetch("../assets/data/ghostProperties.json").then((t=>t.json())).then((t=>{monsterData=t[getRandomInt(2)]})).catch((t=>{console.error("Error loading JSON:",t)}));const emfLights=document.querySelectorAll(".emf-light-bulb");function addHostility(){monsterData.hostility<5&&(monsterData.hostility+=1),emfLights.forEach(((t,o)=>{monsterData.hostility>=o&&t.classList.remove("emf-light-off")}))}function removeHostility(){monsterData.hostility>0&&(monsterData.hostility-=1),emfLights.forEach(((t,o)=>{monsterData.hostility<o&&t.classList.add("emf-light-off")}))}