const scrollableGlyphDivs=document.getElementsByClassName("glyph-list");Array.from(scrollableGlyphDivs).forEach((s=>{s.addEventListener("scroll",(function(){0===s.scrollTop?(s.classList.remove("scroll-up"),s.classList.add("scroll-down")):s.scrollTop+s.clientHeight===s.scrollHeight?(s.classList.add("scroll-up"),s.classList.remove("scroll-down")):(s.classList.add("scroll-down"),s.classList.add("scroll-up"))}))})),$(".glyph-list").each((function(){const s=$(this).find(".glyph-item");s.click((function(){s.removeClass("active"),$(this).addClass("active")}))}));