const scrollablePortalDivs=document.getElementsByClassName("portal-list");Array.from(scrollablePortalDivs).forEach((s=>{s.addEventListener("scroll",(function(){0===s.scrollTop?(s.classList.remove("scroll-up"),s.classList.add("scroll-down")):s.scrollTop+s.clientHeight>s.scrollHeight-10?(s.classList.add("scroll-up"),s.classList.remove("scroll-down")):(s.classList.add("scroll-down"),s.classList.add("scroll-up"))}))})),$(".portal-list").each((function(){const s=$(this).find(".portal-item");s.click((function(){var l=$(this);l.hasClass("active")?l.removeClass("active"):(s.removeClass("active"),l.addClass("active"))}))}));