const polyline=document.getElementById("line");function jitterPolylineY(t,n,e,o){const i=t.points,l=i.length,s=Math.floor(l/2);let a=!1;const c=()=>{if(!o)return;const t=Date.now()*e,m=a?-1:1;for(let e=1;e<l-1;e++){const o=Math.abs(e-s),l=n*(1-o/s)*m,a=Math.sin(t+e)*l,c=Math.max(0,Math.min(100,i[e].y+a));i[e].y=c}a=!a,setTimeout((()=>{requestAnimationFrame(c)}),33)};c()}const listenButton=document.getElementById("listen");listenButton.addEventListener("click",(()=>{jitterPolylineY(polyline,3,100,!0)}));